  const toggleNavLinks = () => {
    navLinksRef.current.classList.toggle("open");
    hamburgerRef.current.classList.toggle("rotate");
  };

  return (
    <nav className="navBar">
      {/* //     <div className='hamburger' ref={hamburgerRef}onClick={toggleNavLinks}>
        //         <div class='line1'></div>
        //         <div class='line2'></div>
        //         <div class='line3'></div>


        //     </div> */}
      <ul className="nav-links" ref={navLinksRef}>
        <li className="nav-li">
          <a className="nav-link one" href="/about">
            About
          </a>
        </li>
        <li className="nav-li">
          <a className="nav-link two" href="/projects">
            Projects
          </a>
        </li>
        <li className="nav-li">
          <a className="nav-link three" href="/contact">
            Logout
          </a>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
